const _fill = (next, prng, array, i = 0, j = array.length) => {
	for (; i < j; ++i) {
		array[i] = next(prng);
	}
};

export default _fill;

import _fill from './_fill.js';
import nextUint8 from './nextUint8.js';
import nextUint16 from './nextUint16.js';
import nextInt32 from './nextInt32.js';
import nextFloat32 from './nextFloat32.js';
import nextFloat64 from './nextFloat64.js';
import nextBigInt64 from './nextBigInt64.js';
import nextBigUint64 from './nextBigUint64.js';

const fill = (prng, array, i, j) => {
	switch (array.constructor) {
		case ArrayBuffer:
			return _fill(nextUint8, new Uint8Array(array), i, j);

		case Int8Array:
		case Uint8Array:
		case Uint8ClampedArray:
			return _fill(
				nextUint8,
				prng,
				new Uint8Array(array.buffer, array.byteOffset, array.byteLength),
				i,
				j,
			);

		case Int16Array:
		case Uint16Array:
			return _fill(
				nextUint16,
				prng,
				new Uint16Array(array.buffer, array.byteOffset, array.length),
				i,
				j,
			);

		case Int32Array:
		case Uint32Array:
			return _fill(
				nextInt32,
				prng,
				new Int32Array(array.buffer, array.byteOffset, array.length),
				i,
				j,
			);

		case Float32Array:
			return _fill(nextFloat32, prng, array, i, j);

		case Float64Array:
			return _fill(nextFloat64, prng, array, i, j);

		case BigInt64Array:
			return _fill(nextBigInt64, prng, array, i, j);

		case BigUint64Array:
			return _fill(nextBigUint64, prng, array, i, j);

		default:
			throw new Error(
				`fill(prng, array, ...): array with constructor '${array.constructor.name}' not implemented`,
			);
	}
};

export default fill;

export default function* genBit(prng) {
	for (const x of prng) {
		yield (x >>> 0) & 1;
		yield (x >>> 1) & 1;
		yield (x >>> 2) & 1;
		yield (x >>> 3) & 1;
		yield (x >>> 4) & 1;
		yield (x >>> 5) & 1;
		yield (x >>> 6) & 1;
		yield (x >>> 7) & 1;
		yield (x >>> 8) & 1;
		yield (x >>> 9) & 1;
		yield (x >>> 10) & 1;
		yield (x >>> 11) & 1;
		yield (x >>> 12) & 1;
		yield (x >>> 13) & 1;
		yield (x >>> 14) & 1;
		yield (x >>> 15) & 1;
		yield (x >>> 16) & 1;
		yield (x >>> 17) & 1;
		yield (x >>> 18) & 1;
		yield (x >>> 19) & 1;
		yield (x >>> 20) & 1;
		yield (x >>> 21) & 1;
		yield (x >>> 22) & 1;
		yield (x >>> 23) & 1;
		yield (x >>> 24) & 1;
		yield (x >>> 25) & 1;
		yield (x >>> 26) & 1;
		yield (x >>> 27) & 1;
		yield (x >>> 28) & 1;
		yield (x >>> 29) & 1;
		yield (x >>> 30) & 1;
		yield (x >>> 31) & 1;
	}
}

import genBit from './genBit.js';

export default function* genBoolean(prng) {
	for (const bit of genBit(prng)) yield Boolean(bit);
}

export default function* genByte(prng) {
	for (const x of prng) {
		yield (x >>> 0) & 0xff;
		yield (x >>> 8) & 0xff;
		yield (x >>> 16) & 0xff;
		yield (x >>> 24) & 0xff;
	}
}

export default function genInt32(prng) {
	return prng;
}

export default function* genUint64(prng) {
	while (true) {
		yield [prng.next().value, prng.next().value];
	}
}

export {default as _fill} from './_fill.js';
export {default as fill} from './fill.js';
export {default as genBit} from './genBit.js';
export {default as genBoolean} from './genBoolean.js';
export {default as genByte} from './genByte.js';
export {default as genInt32} from './genInt32.js';
export {default as genUint16} from './genUint16.js';
export {default as genUint64} from './genUint64.js';
export {default as nextBigInt64} from './nextBigInt64.js';
export {default as nextBigUint64} from './nextBigUint64.js';
export {default as nextFloat32} from './nextFloat32.js';
export {default as nextFloat64} from './nextFloat64.js';
export {default as nextInt32} from './nextInt32.js';
export {default as nextUint16} from './nextUint16.js';
export {default as nextUint32} from './nextUint32.js';
export {default as nextUint64} from './nextUint64.js';
export {default as nextUint8} from './nextUint8.js';
export {default as splitmix64} from './splitmix64.js';
export {default as xoroshiro128plus} from './xoroshiro128plus.js';

import nextBigUint64 from './nextBigUint64.js';

export default function nextBigInt64(prng) {
	return nextBigUint64(prng) - BigInt(2 ** 63);
}

export default function nextBigUint64(prng) {
	const a = prng.next().value;
	const b = prng.next().value;
	return BigInt(a) * BigInt(2 ** 32) + BigInt(b);
}

export default function nextFloat32(prng) {
	const x = prng.next().value;
	return (x >>> 9) / 2 ** 23;
}

export default function nextFloat64(prng) {
	const a = prng.next().value;
	const b = prng.next().value;
	return (a >>> 0) / 2 ** 32 + (b >>> 0) / 2 ** 64;
}

export default function nextInt32(prng) {
	return prng.next().value;
}

export default function nextUint32(prng) {
	return prng.next().value >>> 0;
}

export default function nextUint64(prng) {
	return [prng.next().value, prng.next().value];
}

export default function nextUint8(prng) {
	return (prng.next().value >>> 24) & 0xff;
}

